/** @jsxImportSource @emotion/react */
import { css } from "@emotion/react";
import Logo from "@/styles/icon/logo";
import { useRouter } from "next/router";
import axios from "axios";
import { useEffect, useState } from "react";
import Image from "next/image";

interface User {
  img: string;
  email: string;
  name: string;
}

export default function Navbar() {
  const router = useRouter();
  const [search, setSearch] = useState("");
  const [user, setUser] = useState<User | null>(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [menu, setMenu] = useState(false);

  const handleGoogleLogin = async () => {
    try {
      const response = await axios.get(
        `${process.env.NEXT_PUBLIC_API_URL}/api/member/authenticate`
      );
      console.log(response.data);
      const loginUrl = response.data;
      window.location.href = loginUrl;
    } catch (error) {
      console.error("Failed to get Google login URL", error);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("authToken");
    setIsLoggedIn(false);
    router.push("/");
  };

  const toggleMenu = () => {
    setMenu((prevMenu) => !prevMenu);
    console.log(menu);
  };

  const handleClick = () => {
    router.push("/");
  };
  const handleSearch = () => {
    if (search.trim()) {
      router.push(`/search/${search}`);
    }
  };

  useEffect(() => {
    const token = localStorage.getItem("authToken");
    if (token) {
      setIsLoggedIn(true);
      const email = localStorage.getItem("email");
      const name = localStorage.getItem("name");
      const picture = localStorage.getItem("picture");
      if (email && name && picture) {
        setUser({ email, name, img: picture });
      }
    }
  }, []);

  useEffect(() => {
    const { code } = router.query;
    if (code) {
      const authenticateUser = async () => {
        try {
          const response = await axios.post(
            `${process.env.NEXT_PUBLIC_API_URL}/api/member/login?code=${code}`,
            { code },
            {
              headers: {
                Accept: "application/json;charset=UTF-8",
              },
            }
          );

          console.log("로그인 성공", response);
          localStorage.setItem("authToken", response.data.tokens.accessToken);
          localStorage.setItem(
            "refreshToken",
            response.data.tokens.refreshToken
          );
          localStorage.setItem("grantType", response.data.tokens.grantType);
          localStorage.setItem("email", response.data.memberDTO.email);
          localStorage.setItem("name", response.data.memberDTO.name);
          localStorage.setItem("picture", response.data.memberDTO.picture);
          setIsLoggedIn(true);
          setUser({
            email: response.data.memberDTO.email,
            name: response.data.memberDTO.name,
            img: response.data.memberDTO.picture,
          });
          router.push("/");
        } catch (error) {
          console.log("로그인 실패", code);
          console.error("Authentication 실패", error);
        }
      };
      authenticateUser();
    }
  }, [router.query]);

  return (
    <div css={container}>
      <div className="logo" onClick={() => handleClick()}>
        <svg
          width="80"
          height="39"
          viewBox="0 0 80 39"
          fill="none"
          xmlns="http://www.w3.org/2000/svg">
          <path
            d="M18.7612 37.9776H25.5522C27.0149 37.9776 29.5 36 29.5 33.3454C29.5 29.5 27 28.7132 25 28.7132C24.4776 28.7132 25.0224 28.5 24.5 28.5C17.5 14.5 16.5 7.99995 13 8.4999C13 9.4999 10 21.4999 6.22388 28.7132H5.1791C2.8806 28.7132 1 30.8087 1 33.3454C1 35.8821 2.8806 37.9776 5.1791 37.9776H10.403H18.7612Z"
            stroke="#A775FF"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
          />
          <path
            d="M17.75 30.7633H13.25L12.4211 33H11L14.7303 23H16.2845L20 33H18.5789L17.75 30.7633ZM17.3651 29.6955L15.5 24.6162L13.6349 29.6955H17.3651Z"
            fill="#A775FF"
          />
          <path
            d="M13.5 0L14.5623 3.05573H18L15.2188 4.94427L16.2812 8L13.5 6.11146L10.7188 8L11.7812 4.94427L9 3.05573H12.4377L13.5 0Z"
            fill="#A775FF"
          />
          <path
            d="M46.4178 21H48.5408V28.4838H46.4178V21ZM47.6788 23.8672H50.4882V25.6328H47.6788V23.8672ZM38.7718 29.0832H48.5408V33.1976H40.9108V34.9795H38.8037V31.6425H46.4497V30.7354H38.7718V29.0832ZM38.8037 34.1533H48.9717V35.838H38.8037V34.1533ZM40.9427 21.4374C43.2093 21.4374 44.8854 22.8467 44.8854 24.8391C44.8854 26.8153 43.2093 28.2408 40.9427 28.2408C38.692 28.2408 37 26.8153 37 24.8391C37 22.8467 38.692 21.4374 40.9427 21.4374ZM40.9427 23.1706C39.8573 23.1706 39.0591 23.7862 39.0591 24.8391C39.0591 25.892 39.8573 26.5238 40.9427 26.5238C42.0441 26.5238 42.8422 25.892 42.8422 24.8391C42.8422 23.7862 42.0441 23.1706 40.9427 23.1706Z"
            fill="#575868"
          />
          <path
            d="M52.2108 21.7613H54.3179V23.6566H57.1272V21.7613H59.2023V28.7754H52.2108V21.7613ZM54.3179 25.2927V27.0583H57.1272V25.2927H54.3179ZM58.7235 24.4179H62.315V26.1512H58.7235V24.4179ZM61.8361 21H63.9591V29.2127H61.8361V21ZM54.1423 29.8445H56.2493V31.189H61.8521V29.8445H63.9591V35.8218H54.1423V29.8445ZM56.2493 32.8413V34.1048H61.8521V32.8413H56.2493Z"
            fill="#575868"
          />
          <path
            d="M69.5926 22.1501H71.3165V24.3369C71.3165 28.0464 70.0395 31.5292 67.2461 32.9222L65.9691 31.189C68.4592 30.0065 69.5926 27.1231 69.5926 24.3369V22.1501ZM70.0235 22.1501H71.7156V24.3369C71.7156 27.0259 72.7691 29.7635 75.1315 30.9298L73.8545 32.6469C71.1888 31.27 70.0235 27.933 70.0235 24.3369V22.1501ZM75.7221 21H77.861V36H75.7221V21ZM77.3822 26.7667H80V28.5648H77.3822V26.7667Z"
            fill="#575868"
          />
          <path
            d="M76.5 15L77.3262 17.2918H80L77.8369 18.7082L78.6631 21L76.5 19.5836L74.3369 21L75.1631 18.7082L73 17.2918H75.6738L76.5 15Z"
            fill="#A775FF"
          />
        </svg>
      </div>

      <div className="nav-right">
        <div className="input">
          <input
            type="text"
            placeholder="검색"
            value={search}
            onChange={(event) => setSearch(event.target.value)}
            onKeyPress={(event) => {
              if (event.key === "Enter") {
                handleSearch();
              }
            }}
          />
        </div>
        <button onClick={handleSearch}>검색</button>

        {isLoggedIn && user && (
          <div className="user-box" onClick={handleLogout}>
            <div className="img-box">
              <Image src={user.img} width={36} height={36} alt="user profile" />
            </div>
            {user.name}
          </div>
        )}

        {!isLoggedIn && (
          <button className="loginbutton" onClick={handleGoogleLogin}>
            <svg
              width="30"
              height="30"
              viewBox="0 0 30 30"
              fill="none"
              xmlns="http://www.w3.org/2000/svg"
              xmlnsXlink="http://www.w3.org/1999/xlink">
              <rect width="30" height="30" fill="url(#pattern0_142_96)" />
              <defs>
                <pattern
                  id="pattern0_142_96"
                  patternContentUnits="objectBoundingBox"
                  width="1"
                  height="1">
                  <use xlinkHref="#image0_142_96" transform="scale(0.00625)" />
                </pattern>
                <image
                  id="image0_142_96"
                  width="160"
                  height="160"
                  xlinkHref="data:image/png;base64,"
                />
              </defs>
            </svg>
            구글 계정으로 로그인
          </button>
        )}
      </div>
      <div className="nav-rightnext" onClick={toggleMenu}>
        <svg
          width="24"
          height="24"
          viewBox="0 0 24 24"
          fill="none"
          xmlns="http://www.w3.org/2000/svg">
          <path
            d="M3 8H21"
            stroke="#BCEB00"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
          />
          <path
            d="M3 16H21"
            stroke="#BCEB00"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
          />
        </svg>
      </div>
    </div>
  );
}
const container = css`
  display: flex;
  padding: 10px 62px;
  width: 100%;
  height: 100%;
  justify-content: space-between;
  align-items: center;
  cursor: pointer;
  button {
    align-items: center;
    background-color: #995dff;
    color: white;
    border-radius: 20px;
    cursor: pointer;
    text-align: center;
    width: 100px;
    height: 40px;
  }

  .slide-menu {
    display: flex;
    padding: 10px 0px;
    height: 100%;
    justify-content: space-between;
    align-items: center;
    cursor: pointer;
    position: fixed;
    width: 250px;
    background-color: white;
    top: 52px;
    right: -250px;
    transition: right 0.3s;
    z-index: 1000;
  }
  ul {
    list-style: none;
    padding: 0;
  }

  li {
    display: flex;
    width: 100%;
    height: 54px;
    padding-left: 50px;
    font-family: Poppins;
    font-size: 12px;
    font-weight: 400;
    line-height: 18px;
    align-items: center;
    border-radius: 0 100px 100px 0;
    cursor: pointer;
  }
  .icon {
    width: 20px;
    height: 20px;
    display: flex;
    justify-content: center;
    align-items: center;
    margin-right: 10px;
  }

  .slide-menu.open {
    right: 0;
  }

  .nav-rightnext {
    display: none;
    cursor: pointer;
  }

  @media (max-width: 550px) {
    .nav-right {
      display: none;
    }
    .nav-rightnext {
      display: block;
    }
  }

  .logo {
    display: flex;
    gap: 10px;
    justify-content: center;
    align-items: center;
    p {
      width: 43px;
      height: 15px;
    }
  }
  .input {
    width: 100%;
    max-width: 1000px;
    height: 40px;
  }
  input {
    width: 100%;
    height: 40px;
    border-radius: 100px;
    border: 1px solid #995dff;
  }
  .nav-right {
    display: flex;
    gap: 50px;
    align-items: center;
    width: 70%;
  }
  .img-box {
    width: 36px;
    height: 36px;
    border-radius: 50%;
    background-color: black;
    overflow: hidden;
  }
  .user-box {
    display: flex;
    gap: 10px;
    align-items: center;
    white-space: nowrap;
  }
  .loginbutton {
    display: flex;
    justify-content: center;
    gap: 10px;
    align-items: center;
    background-color: #995dff;
    color: white;
    width: 400px;
    height: 42px;
    border: none;
    border-radius: 20px;
    cursor: pointer;
    outline: none;
    font-family: Roboto;
    font-size: 15px;
    font-weight: 400;
    line-height: 17.58px;
    text-align: center;
  }
  .nav-rightnext {
    display: none;
    cursor: pointer;
  }
  @media (max-width: 550px) {
    .nav-right {
      display: none;
    }
    .nav-rightnext {
      display: block;
    }
  }
`;

const activeCategoryStyle = css`
  background-color: #e7ff85;
`;
